#homePage {
    display: flex;
    width: 100%;
    padding: 30px;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    height: auto;
    min-height: 85vh;
}
#info {
    display: flex;
    height: 500px;
    padding: 10px 0;
    align-items: flex-start;
    justify-content: flex-start; 
    gap: 20px;
    flex-shrink: 0;
    align-self: stretch;
    overflow-x: hidden;
    width: 100%;
}

#newsFrame {
    display: flex;
    padding: 10px 0;
    align-items: flex-start;
    gap: 20px;
    flex: 1 0 0;
    align-self: stretch;
}

#alerts {
    display: flex;
    width: 50%;
    padding: 15px;
    flex-direction: column;
    align-items: center;
    gap: 15px;
    align-self: stretch;
    border-radius: 20px;
    background: rgba(90, 90, 90, .7);
    
}

#status {
    display: flex;
    width: 50%;
    padding: 15px;
    flex-direction: column;
    align-items: center;
    gap: 15px;
    align-self: stretch;
    border-radius: 20px;
    background: rgba(90, 90, 90, .7);
}

.header {
    display: flex;
    padding: 0 0px 8px 0px;
    justify-content: center;
    align-items: center;
    align-self: stretch;
    border-bottom: 3px solid #FFF;
}

.iconBox {
    display: flex;
    width: 60px;
    height: 61px;
    padding: 14px;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 10px;
    flex-shrink: 0;
    aspect-ratio: 60/61;
    border-radius: 50px;
    background: #AAA;
}
.messageTextBold {
    font-weight: 700;
}

.messageText {
    color: #FFF;
    font-family: Inter;
    font-size: 24px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
}

.message {
    display: flex;
    height: 80px;
    padding: 10px 18px;
    align-items: center;
    gap: 10px;
    border-radius: 25px;
}

.statusMessage {
    width: 100%;
}

.alertMessage {
    width: 100%;
}

.red {
    background: linear-gradient(91deg, #4B2828 0%, #6E3D3D 100%);
}

.green {
    background: linear-gradient(91deg, #284B28 0%, #3F6E3D 100%);
}

.blue {
    background: linear-gradient(91deg, #28384B 0%, #3D546E 100%);
}

.newsTemplate {
    display: flex;
    padding: 10px;
    justify-content: center;
    align-items: center;
    gap: 10px;
    flex: 1 0 0;
    align-self: stretch;
    border-radius: 20px;
    background: #afafaf;
}

.newsImageDiv {
    flex: 0 0 35%;
    display: flex;
    justify-content: center;
    align-items: center;
    min-width: 0;
}

.newsImageDiv img {
    width: 100%;
    max-width: 100%;
    height: auto;
    aspect-ratio: 23 / 31;
    border-radius: 15px;
    object-fit: cover;
}

.newsInfo {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
    flex: 1 0 0;
    align-self: stretch;
}

.newsDate{
    display: flex;
    height: 31px;
    flex-direction: column;
    justify-content: center;
    align-self: stretch;
    color: #000;
    font-family: Inter;
    font-size: 20px;
    font-style: normal;
    font-weight: 200;
    line-height: normal;
}

.newsSource {
    display: flex;
    height: 31px;
    flex-direction: column;
    justify-content: center;
    align-self: stretch;
    color: #000;
    font-family: Inter;
    font-size: 18px;
    font-style: normal;
    font-weight: 200;
    line-height: normal;
}

.newsText {
    flex: 1 0 0;
    align-self: stretch;
    color: #000;
    font-family: Inter;
    font-size: 25px;
    font-style: normal;
    font-weight: 300;
    line-height: normal;
